	TEXT_SetFont(hItem,&HZ_SONGTI_16);
	TEXT_SetText(hItem,g_ucaLng_Serve_TotalPlace[MachInfo.systemSet.eLanguage]);
    //
    // Initialization of 'totalPlaceNum_t'
    //
    hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_5);
    TEXT_SetTextAlign(hItem, GUI_TA_RIGHT | GUI_TA_VCENTER);
	TEXT_SetFont(hItem,&HZ_SONGTI_16);
	sprintf(StrTemp,"%.2f MB",TotalNum/2/1024.0);
	TEXT_SetText(hItem,StrTemp);
    //
    // Initialization of 'usedPlace_t'
    //
    hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_6);
    TEXT_SetTextAlign(hItem, GUI_TA_LEFT | GUI_TA_VCENTER);
