	TEXT_SetText(hItem,g_ucaLng_Serve_UsedPlace[MachInfo.systemSet.eLanguage]);
    //
    // Initialization of 'usedPlaceNum_t'
    //
    hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_7);
    TEXT_SetTextAlign(hItem, GUI_TA_RIGHT | GUI_TA_VCENTER);
	TEXT_SetFont(hItem,&HZ_SONGTI_16);
	memset(StrTemp,0,20);
	sprintf(StrTemp,"%.2f MB",(TotalNum-FreeNum)/2/1024.0);
	TEXT_SetText(hItem,StrTemp);
	
	//设置按钮皮肤
	hItem = WM_GetDialogItem(pMsg->hWin, ID_BUTTON_0);
	BUTTON_SetFont(hItem,&HZ_SONGTI_16);
	BUTTON_SetTextAlign(hItem,GUI_TA_HCENTER|GUI_TA_VCENTER);
